INSERT INTO `products` (`product_id`, `name`, `price`, `description`, `discount`, `image`, `quantity`, `category_id`, `supplier_id`) VALUES (1, 'Phở thuyền', 100, 'Phở là một món ăn truyền thống của Việt Nam, có nguồn gốc từ Hà Nội và được xem là một trong những món ăn tiêu biểu cho nền ẩm thực Việt Nam.\r\n\r\nThành phần chính của phở là bánh phở và nước dùng cùng với thịt bò hoặc thịt gà cắt lát mỏng. Thịt bò thích hợp nhất để nấu phở là thịt, xương từ các giống bò ta (bò nội, bò vàng). Ngoài ra còn kèm theo các gia vị như: tương, tiêu, chanh, nước mắm, ớt, vân vân. Những gia vị này được thêm vào tùy theo khẩu vị của người dùng. Phở thông thường được dùng để làm món điểm tâm buổi sáng hoặc lót dạ buổi đêm; nhưng ở các thành phố lớn, món ăn này có thể được thưởng thức cả ngày. Tại các tỉnh phía Nam Việt Nam và một số vùng miền khác, phở được bày kèm với đĩa rau thơm như hành, giá và những lá cây rau mùi, rau húng, trong đó ngò gai là loại lá đặc trưng của phở; tuy nhiên tại Hà Nội thông thường sẽ không có đĩa rau sống này. Phở thường là phở bò hay phở gà, nhưng đôi khi cũng có những biến thể khác, đặc biệt là phở sốt vang, phở khô, phở xào, phở chua, phở vịt ở Cao Bằng, và phở thịt quay ở các tỉnh miền núi phía Bắc.', 10, 'https://d1sag4ddilekf6.cloudfront.net/compressed_webp/items/VNITE2022021907451144733/detail/menueditor_item_0afb4c58cb904557aa421c23aa87d956_1645259828713679129.webp', 100, 5, 1), (2, 'Bún bò nước lèo vị trà sữa', 50, 'Món bún bò truyền thống với nước dùng thơm ngon được các bạn trẻ mua về, nhưng họ chỉ sử dụng phần bún và đồ ăn phía trên, còn nước dùng thì được thay thế bằng ly trà sữa trân châu kế bên.', 20, 'data:image/jpeg;base64,', 100, 5, 2), (3, 'Bia Sài Gòn', 15, 'Bia Sài Gòn từ lâu đã trở thành một nhãn hiệu bia quen thuộc tại Việt Nam. Đây là nhãn hàng do SABECO sản xuất và phân phối ra thị trường.', 5, 'data:image/jpeg;base64,', 100, 1, 3), (4, 'Nước cam ép', 20, 'Nước ép cam là một loại nước ép vô cùng phổ biến và được nhiều người yêu thích bởi sự chua ngọt, dễ uống cũng như hỗ trợ nguồn vitamin C giúp tăng sức đề ...', 10, 'https://suckhoedoisong.qltns.mediacdn.vn/324455921873985536/2022/2/19/cach-lam-nuoc-cam-ep-ngon-va-thom-ket-hop-voi-le-va-gung-5-1645248090817401855254.jpg', 100, 1, 4), (5, 'Nước chanh muối', 10, 'Nước chanh muối Thạch Bích là sự kết hợp tuyệt vời giữa nước khoáng thiên nhiên có nhiều vi khoáng, CO2 thực phẩm, đường Saccaroza với hương vị chanh muối độc ...', 20, 'https://cdn.tgdd.vn/Products/Images/3226/128831/bhx/6-chai-nuoc-bu-khoang-revive-chanh-muoi-390ml-202103272142426493.jpg', 100, 1, 1), (6, 'Cơm chiên', 30, 'Cơm chiên là một món ăn quen thuộc mà chúng ta dùng trong bữa ăn nào cũng được, nếu cơm nguội còn thừa nhiều thì chị em thường tận dụng làm các món cơm chiên tuyệt ngon cho cả nhà cùng thưởng thức. Cũng là cơm chiên thôi nhưng thêm chút biến tấu là đã trở thành món có sức hút biết bao, vào bếp cùng Cooky để bỏ túi những món cơm chiên ngon lành nhé!', 10, 'https://i.ytimg.com/vi/FR4DH5sSysI/maxresdefault.jpg', 100, 2, 2), (7, 'Cơm rang dưa bò', 40, 'Cơm rang dưa bò là món ngon đang được rất nhiều người ưa chuộng với cách chế biến rất đơn giản và nhanh chóng, tiện lợi.', 20, 'https://pastaxi-manager.onepas.vn/content/uploads/articles/mon-an-vat-hai/com-rang-dua-bo/cach-lam-com-rang-dua-bo-1.jpg', 100, 2, 3), (8, 'Cơm gà', 35, 'Gà là một món ăn quen thuộc và không thể thiếu trong cuộc sống của mỗi người Việt Nam chúng ta. Gà là nguyên liệu cực kỳ đa dạng bạn có thể dùng nó để chế biến rất nhiều món ngon. Sau đây hãy cùng với The Water MAN tìm hiểu về 5 công thức để làm một món cơm gà ngon cực chuẩn và chất lượng tuyệt vời nhé.', 10, 'https://media.foody.vn/res/g80/791257/prof/s/file_restaurant_photo_zj7d_16195-88cb1e38-210427122724.jpg', 100, 2, 4), (9, 'Khoai tây chiên', 25, 'Khoai tây chiên là một món ăn rất được mọi người ưa thích, bởi hương vị thơm ngon và cách làm đơn giản. Món khoai tây chiên ngon nhất khi có lớp vỏ ngoài giòn rụm, bên trong thì chín mềm. Với cách làm trong bài viết này, bạn sẽ có một đĩa khoai tây chiên để lâu vẫn giữ được độ giòn, ngon đấy.', 5, 'https://cdn.tgdd.vn/Files/2015/03/01/615221/bi-quyet-lam-moi-khoai-tay-chien-cu-5-760x367.jpg', 95, 3, 1), (10, 'Cá viên chiên', 30, 'Cá viên chiên tiếng anh còn gọi là Curry fish ball, đây là món ăn vặt “huyền thoại” của không biết bao nhiêu bạn trẻ từ xưa cho đến nay. Với cách làm cá viên chiên giòn mà Cet.edu.vn hướng dẫn sau đây thì bạn có thể chiêu đãi cả nhà món ăn này khi sum họp vào mỗi tối hoặc có thể cùng hội bạn vừa trò chuyện vừa nhấm nháp cũng khá thú vị phải không nào?', 10, 'https://images.foody.vn/res/g100006/1000051064/prof/s1242x600/file_restaurant_photo_etep_16507-d9fb24f0-220423193357.jpeg', 100, 3, 2), (11, 'Gà rán', 50, 'Hiện nay, có rất nhiều người săn tìm cách làm gà rán để chế biến tại nhà thay đổi khẩu vị của gia đình. Đặc biệt, mẻ gà rán giòn rụm, vàng ươm đẹp mắt còn rất được lòng các em nhỏ nữa đấy.', 5, 'https://lh3.googleusercontent.com/pVS28hUcRq7vZhNRCzrCnQ0rJa3MYb3uKPbgYdaeHLUWJOMN5uaKsZHJpHX1xJGJPyx_4GtrGqB-wJKYM-bzobAKKU14HkCg69RqcCGyqFW6I4Z4lebm3Z3qnqxe8dOE07bSh3-MlEcIdDjfae_oIuYYxEsqdtdLws0iwzQzSVCZmCgreSrv4-jL0oDGCxHLMbriVjNsJzQxhH-l4YzwRYDYD5TbNls-21O76N-8-J3sSW1VgolboH5J92djm3FAvc_sFvcu6g-QbQCA-qd2Vtl_4q2hfSgwKmggpe5T4d5_ZwKKeYdTgri_Fn1YMpZxs59G3hxjMDZRu7BmnFCqR4EOYAiPSvjVswjoT71gNSdjTyCH90qk45-n8JN39YuP7EUluhrQAa9r-Iv3oQxIkPtPBRlcF87AUrCXf-MMf-9yIABFxydkYuDZR1y0ub9XgmMttWVJJABBnv-J-Z4v6r0QuzTDORM5ZgvElJ8_Jx1Y99zW9qW9FD6TuzTbAz9PtSCu4GYBuVz9PfMNXW-kCJh_OdnbptiInf1CbADQHCdC5-WkXAVwxTD6wZJzhcUPu6EcvjrYExSsg3rkH8ra2II9DgKapZ581UtRVFGuZBekdSX6LxqoYYp_jHf8pq7LWvGdc2pxWyaCzFilkJlYL5k1GqeQB6aOwe2Fa-8IsuzGnIaEEdbAcVmsh2VmBsdyk6inzzeAt_HSfxMW9bdWRbVuXmwFEnb8wRmoCS5sdJhxXsTB_-PcInDyxqwWa8mLyYXOPm8nCXqQJWKKC1MBSLHD1oprpIjuzSv0U5ANIFwW7syjRcJY6cJy7B7K-bkWHY_t91jz2IwakeBxenlZzTw-I8Wlv9h_LTFDX3u5Kbi9Oqniw1ccceErrYDU_D8uMdqg_Ky6RVVj3vu5aM7cZw08dtudu0u9906_XiNs7S8oABfOoINWOaPWXgRumRRHPn6_ou54DqI4K8_qg_M=w659-h879-s-no?authuser=0', 1