-- ----------------------------
-- View structure for bill_view
-- ----------------------------
DROP VIEW IF EXISTS `bill_view`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `bill_view` AS select `o`.`order_id` AS `order_id`,`c`.`customer_id` AS `customer_id`,`c`.`fullname` AS `fullname`,`c`.`phone` AS `phone`,`od`.`address` AS `address`,`o`.`order_date` AS `order_date`,group_concat(concat(`pr`.`name`,' (Giá: ',round((`pr`.`price` - ((`pr`.`price` * `pr`.`discount`) / 100)),3),', Số lượng: ',`od`.`quantity`,')') separator ', ') AS `product_list`,format(`o`.`total_price`,3) AS `total_price` from (((`orders` `o` join `customers` `c` on((`o`.`customer_id` = `c`.`customer_id`))) join `order_details` `od` on((`o`.`order_id` = `od`.`order_id`))) join `products` `pr` on((`od`.`product_id` = `pr`.`product_id`))) group by `o`.`order_id`,`c`.`customer_id`,`c`.`fullname`,`c`.`phone`,`od`.`address`,`o`.`order_date`,`o`.`total_price`;

-- ----------------------------
-- View structure for cart_product_view
-- ----------------------------
DROP VIEW IF EXISTS `cart_product_view`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `cart_product_view` AS select `c`.`cart_id` AS `cart_id`,`c`.`customer_id` AS `customer_id`,`c`.`product_id` AS `product_id`,`c`.`quantity` AS `quantity`,`p`.`name` AS `name`,`c`.`price` AS `price`,`p`.`image` AS `image`,`p`.`discount` AS `discount` from (`carts` `c` join `products` `p` on((`c`.`product_id` = `p`.`product_id`)));

