DROP TRIGGER IF EXISTS `create_cart_for_new_customer`;
delimiter ;;
CREATE TRIGGER `create_cart_for_new_customer` AFTER INSERT ON `customers` FOR EACH ROW BEGIN
    INSERT INTO carts (customer_id) VALUES (NEW.customer_id);
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table order_details
-- ----------------------------
DROP TRIGGER IF EXISTS `decrease_quantity`;
delimiter ;;
CREATE TRIGGER `decrease_quantity` AFTER INSERT ON `order_details` FOR EACH ROW BEGIN
    -- Giảm số lượng sản phẩm khi có đơn hàng được tạo
    UPDATE products
    SET quantity = quantity - NEW.quantity
    WHERE product_id = NEW.product_id;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table order_details
-- ----------------------------
DROP TRIGGER IF EXISTS `increase_quantity`;
delimiter ;;
CREATE TRIGGER `increase_quantity` AFTER DELETE ON `order_details` FOR EACH ROW BEGIN
    -- Tăng lại số lượng sản phẩm khi đơn hàng bị hủy
    UPDATE products
    SET quantity = quantity + OLD.quantity
    WHERE product_id = OLD.product_id;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table orders
-- ----------------------------
DROP TRIGGER IF EXISTS `orders_before_insert`;
delimiter ;;
CREATE TRIGGER `orders_before_insert` BEFORE INSERT ON `orders` FOR EACH ROW BEGIN 
  IF NEW.order_date IS NULL THEN 
    SET NEW.order_date = NOW(); 
  END IF; 
END
;;
delimiter ;

